-- 比较两个数字的 if ... else 语句
x = 10;
y = 20;
if (x > y) then
	-- 要执行的语句

-- 比较两个数字的 if 语句
x = 10;
y = 20;
if (x > y) then
	-- 要执行的语句

-- 比较两个不相等数字的 if 语句
x = 10;
y = 20;
if (x ~= y) then
	-- 要执行的语句

-- repeat ... until 循环
numLoopCount = 0;
repeat
	-- 要执行的语句
	numLoopCount = numLoopCount + 1;

-- while 循环
testvar = 10;
while (testvar < 100) do
	-- 要执行的语句
	testvar = testvar + 1;
